import React, { useState, useEffect, useRef } from 'react';
type Farmer = {
  id: string;
  name: string;
  phone: string;
  village: string;
  farmSize: string;
  experience: string;
  organicCertified: boolean;
  currentBatches: Array<{
    id: string;
    herbName: string;
    herbNameHi: string;
    plantingDate: string;
    harvestDate: string;
    quantity?: string;
    stage: string;
    stageHi: string;
    paymentStatus: string;
    paymentStatusHi: string;
    expectedAmount?: string;
    paidAmount?: string;
    photos?: string[];
    iotData?: {
      soilMoisture?: string;
      temperature?: string;
      humidity?: string;
      pH?: string;
      lastUpdated?: string;
    };
    gpsLocation?: { lat: number; lng: number };
    journey?: Array<{ stage: string; date: string; status: string }>;
  }>;
  pastBatches: Array<{
    id: string;
    herbName: string;
    herbNameHi: string;
    harvestDate?: string;
    quantity?: string;
    stage: string;
    stageHi: string;
    paymentStatus?: string;
    paymentStatusHi?: string;
    finalAmount?: string;
    photos?: string[];
    iotData?: {
      soilMoisture?: string;
      temperature?: string;
      humidity?: string;
      pH?: string;
      lastUpdated?: string;
    };
    gpsLocation?: { lat: number; lng: number };
    journey?: Array<{ stage: string; date: string; status: string }>;
  }>;
};

